/*
 * Get the "featured image" or a placeholder if no "featured image" exists
 * @array $size width,height of the placeholder (optional)
 * @bool  $echo spit out code, or return it for use in a variable
 */

function ll_get_featured($size=[158,100],$echo=false) {
  $output = get_the_post_thumbnail();
  if( !$output ) {
    $output = '<img alt="" src="http://via.placeholder.com/'.$size[0].'x'.$size[1].'">';
  }

  if( $echo === true ) {
    echo $output;
  }else{
    return $output;
  }
}

